% clear all;close all;if exist('Nmodel')==0;Nmodel=importdata('/Users/maxstev/Documents/Grad_School/PIRE/CFM/CommunityFirnModel/code/gasmodel/Results/AGU/conc_out_d15N2.csv');Armodel=importdata('/Users/maxstev/Documents/Grad_School/PIRE/CFM/CommunityFirnModel/code/gasmodel/Results/AGU/conc_out_d40Ar.csv');Ndata=importdata('/Users/maxstev/Documents/Grad_School/PIRE/CFM/CommunityFirnModel/code/gasmodel/Results/GISP2_YD_d15N.txt');Ardata=importdata('/Users/maxstev/Documents/Grad_School/PIRE/CFM/CommunityFirnModel/code/gasmodel/Results/GISP2_YD_d40Ar.txt');gisp2age2=importdata('/Users/maxstev/Documents/Grad_School/PIRE/CFM/CommunityFirnModel/code/gasmodel/Results/gisp2age2.csv');% gisp2age2a=importdata('/Users/maxstev/Documents/Grad_School/PIRE/CFM/CommunityFirnModel/code/gasmodel/Results/countage2.txt');% gisp2age2=gisp2age2a.data;endNmodeltime=Nmodel(:,1);Armodeltime=Armodel(:,1);depth=gisp2age2(:,1);age=gisp2age2(:,2);N_data_depth=Ndata(:,1);N_data_c=Ndata(:,2)/1000;N_data_age=interp1(depth,age,N_data_depth)-900;Ar_data_depth=Ardata(:,1);Ar_data_c=Ardata(:,2)/1000;Ar_data_age=interp1(depth,age,Ar_data_depth)-900;ind=find(Nmodeltime>-20000 & Nmodeltime<-10000);Nmodel_YD = Nmodel(ind,2:end);Armodel_YD = Armodel(ind,2:end);Nmodel_time = Nmodeltime(ind);Armodel_time = Armodeltime(ind);Nmodel_max = max(Nmodel_YD,[],2)-1;Armodel_max = max(Armodel_YD,[],2)-1;figure(1);hold on;box on;plot(-1*Nmodel_time,Nmodel_max)plot(N_data_age,N_data_c,'k.')plot(-1*Armodel_time,Armodel_max/4,'r')plot(Ar_data_age,Ar_data_c,'c.')xlim([11000 16000])